mapper.select:
select id, name, category_id, price, code, image, description, status, sort, create_time, update_time, create_user, update_user, is_deleted from dish
and name like concat('%',#{DishDO.name,jdbcType=VARCHAR},'%')
and is_deleted = #{DishDO.isDeleted}
limit #{startIndex}, #{pageSize}

select count(1)
from dish


        and id = #{id}


        and name like concat('%',#{name,jdbcType=VARCHAR},'%')


        and category_id = #{categoryId}


        and price = #{price}


        and code = #{code}


        and image = #{image}


        and description = #{description}


        and status = #{status}


        and sort = #{sort}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and is_deleted = #{isDeleted}

select count(1)
from dish


        and is_deleted = #{isDeleted}

mapper.update:
update dish
set status = #{newStatus}
where id in

    #{id}
